INSERT INTO academy_award(year, category, nominee, description, win) VALUES (	'2004 (77th)',	'Scientific and Technical (Special Awards)',	'To Arthur Widmer for his lifetime of achievement in the science and technology of image compositing for motion pictures as exemplified by his significant contributions to the development of the Ultra Violet and the bluescreen" compositing processes."',	'',	TRUE	);
