INSERT INTO academy_award(year, category, nominee, description, win) VALUES (	'2003 (76th)',	'Scientific and Technical (Academy Award of Merit)',	'To DIGIDESIGN for the design, development and implementation of the Pro Tools® digital audio workstation. The efficient algorithms, extensible architecture and intuitive interface have enabled Pro Tools to become the worldwide standard for the creation and editing of motion picture soundtracks. [Sound]',	'',	TRUE	);
INSERT INTO academy_award(year, category, nominee, description, win) VALUES (	'2003 (76th)',	'Scientific and Technical (Academy Award of Merit)',	'To BILL TONDREAU of Kuper Controls for his significant advancements in the field of motion control technology for motion picture visual effects. Measuring his valuable contributions to the invention and implementation of robotic camera systems in decades rather than years, his efforts have aided motion control in becoming a core technology that has supported the renaissance of visual effects. [Systems]',	'',	TRUE	);
INSERT INTO academy_award(year, category, nominee, description, win) VALUES (	'2003 (76th)',	'Scientific and Technical (Scientific and Engineering Award)',	'To KINOTON GmbH for the engineering and development of the Kinoton FP 30/38 EC II Studio Projector. This high-speed studio projector produces an image quality equal to projectors with Geneva movements. With its unparalleled shuttle speed, reversibility and acceleration, this projector has set a new standard for post-production viewing as well as in traditional screening facilities. [Projection]',	'',	TRUE	);
INSERT INTO academy_award(year, category, nominee, description, win) VALUES (	'2003 (76th)',	'Scientific and Technical (Scientific and Engineering Award)',	'To KENNETH L. TINGLER, CHARLES C. ANDERSON, DIANE E. KESTNER and BRIAN A. SCHELL of the Eastman Kodak Company for the successful development of a process-surviving antistatic layer technology for motion picture film. This technology successfully controls the static charge buildup on processed intermediate and sound negative films during high speed printing operations. [Laboratory]',	'',	TRUE	);
INSERT INTO academy_award(year, category, nominee, description, win) VALUES (	'2003 (76th)',	'Scientific and Technical (Scientific and Engineering Award)',	'To CHRISTOPHER ALFRED, ANDREW J. CANNON, MICHAEL C. CARLOS, MARK CRABTREE, CHUCK GRINDSTAFF and JOHN MELANSON for their significant contributions to the evolution of digital audio editing for motion picture post production. Through their respective pioneering efforts with AMS AudioFile, Waveframe and Fairlight, the work of these gentlemen contributed significantly to the development and realization of digital audio workstations with full editing capabilities for motion picture soundtracks. [Digital Imaging Technology]',	'',	TRUE	);
INSERT INTO academy_award(year, category, nominee, description, win) VALUES (	'2003 (76th)',	'Scientific and Technical (Technical Achievement Award)',	'To KISH SADHVANI for the concept and optical design, PAUL DUCLOS for the practical realization and production engineering and CARL PERNICONE for the mechanical design and engineering of the portable cine viewfinder system known as the Ultimate Director''s Finder (UDF). This versatile, modular and widely accepted cine viewfinder system is capable of properly displaying images in multiple formats ranging from 35mm anamorphic to super 16. [Photography]',	'',	TRUE	);
INSERT INTO academy_award(year, category, nominee, description, win) VALUES (	'2003 (76th)',	'Scientific and Technical (Technical Achievement Award)',	'To HENRIK WANN JENSEN, STEPHEN R. MARSCHNER and PAT HANRAHAN for their pioneering research in simulating subsurface scattering of light in translucent materials as presented in their paper A Practical Model for Subsurface Light Transport." This mathematical model contributed substantially to the development and implementation of practical techniques for simulating subsurface scattering of light in translucent materials for computer-generated images in motion pictures. [Digital Imaging Technology]"',	'',	TRUE	);
INSERT INTO academy_award(year, category, nominee, description, win) VALUES (	'2003 (76th)',	'Scientific and Technical (Technical Achievement Award)',	'To CHRISTOPHE HERY, KEN McGAUGH and JOE LETTERI for their groundbreaking implementations of practical methods for rendering skin and other translucent materials using subsurface scattering techniques. These groundbreaking techniques were used to create realistic-looking skin on digitally created characters. [Digital Imaging Technology]',	'',	TRUE	);
